import logo from './logo.svg';
import './App.css';
import { useEffect } from 'react';

function App() {

  useEffect(()=>{
    let url = `https://kobis.or.kr/kobisopenapi/webservice/rest/boxoffice/searchDailyBoxOfficeList.json?key=f5eef3421c602c6cb7ea224104795888&targetDt=20231205`

    // API 데이터를 주고받을 수 있는 문법 2가지 : fetch, axios -> 비동기 라이브러리
    // case 1) fetch API

    fetch(url)
     // url연결이 되면 데이터를 가져올거야 근데 먼저 json형태로 변환해주고 
    .then(res => res.json())
    .then(res => setMovieList(res.boxOfficeResult.dailyBoxOfficeList))
},[]);

  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <p>
          Edit <code>src/App.js</code> and save to reload.
        </p>
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a>
      </header>
    </div>
  );
}

function setMovieList(responseData) {
  console.log(responseData);
}

export default App;
